import com.eviware.soapui.support.XmlHolder
//This is to fetch the response from a testCase
//Context with is tofetch the response or requet within testcase
def res = context.testCase.getTestStepByName('DeletByid').getPropertyValue("Response")
//To stro this response in a holder XmlHolder
def holder = new XmlHolder(res)
def Result = holder.getNodeValue("//*:result")

log.info "$Result"

if (Result=='true')
{
	log.info "Test case pass"
}

else{
	log.info "Test case fail "
}
