con:config.script:
import jxl.*
def email,firstname, lastname;
String filepath="C:\\Users\\RahilAkram\\Documents\\sampledata.xls"
File Excel = new File(filepath)
// This is the excel with contains workbook and workbook contains sheet
Workbook wb = Workbook.getWorkbook(Excel)
//i want to fetch the sheet
Sheet sh=wb.getSheet("Sheet1")
def rowsize = sh.getRows().toInteger()
def columnsize = sh.getColumns().toInteger()

log.info "The Total number of rows are "+rowsize
log.info "The Total number of columns are "+columnsize

//i want to print the rows and coulmn? what should i do please suggest
for(def i=0;i<rowsize;i++)
{
	for(def j=0;j<columnsize;j++)
	{
		if(j==0){
			 email=sh.getCell(j,i).getContents()
		}
		else
		{
			 firstname=sh.getCell(j,i).getContents()
		}
	}
//This is to go to testCase
def tc =testRunner.testCase.testSuite.project.testSuites['TestSuite 1'].testCases["End to End"]
//Set the username and password in the property file which is fetch it from excel
tc.testSteps["SaveCoustomer"].setPropertyValue("email",email)
tc.testSteps["SaveCoustomer"].setPropertyValue("firstname",firstname)
tc.testSteps["SaveCoustomer"].setPropertyValue("lastname",lastname)
log.info "email is $email"
log.info "firstname is $firstname"
log.info "lastname is $firstname"
def runner = tc.run(null,false)
log.info "Number of test Step got executed"+runner.results.size()
}

